package com.example.EasyProject.repository;

import com.example.EasyProject.model.Boat;

import java.util.ArrayList;
import java.util.List;

public class BoatRepository {
    private List<Boat> list = new ArrayList<>();

    public BoatRepository(){
        Boat boat1 = new Boat();
        boat1.setId(1l);
        boat1.setTitle("Super mega speed boat");
        boat1.setRentalPrice(350);
        boat1.setPurchasePrice(23000);
        boat1.setSpeed(112);
        boat1.setSeating(2);
        boat1.setMaxTransportedWeight(250);
        boat1.setImageURL("data:image/jpeg;base64,");
        boat1.setDescription("файна швидка лодочка");

        Boat boat2 = new Boat();
        boat2.setId(2l);
        boat2.setTitle("Fish hunter numbus 2000");
        boat2.setRentalPrice(225);
        boat2.setPurchasePrice(15000);
        boat2.setSpeed(79);
        boat2.setSeating(4);
        boat2.setMaxTransportedWeight(478);
        boat2.setImageURL("data:image/jpeg;base64,");
        boat2.setDescription("файна швидка лодочка");

        list.add(boat1);
        list.add(boat2);
    }

    public void save(Boat boat) {
        list.add(boat);
    }

    public Boat findById(Long id) {
        for (Boat boat: list) {
            if (boat.getId() == id)
                return boat;
        }
        return null;
    }

    public List<Boat> findAll(){
        return list;
    }
}
